def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:

	# 1. Find the frequency of each number
	freq = collections.Counter(list(collections.Counter(nums).values()))

	# 2. If the quantity[i] is in freq, use that number.
	#    i.e. quantity = [2, 2] ; nums = [1, 1, 2, 3] use [1, 1] right away
	#         so we get quantity = [2] and nums = [2, 3]
	removed = set()
	for i in range(len(quantity)):
		if freq[quantity[i]] > 0:
			freq[quantity[i]] -= 1
			removed.add(i)
	quantity = [quantity[i] for i in range(len(quantity)) if i not in removed]

	if not quantity: return True 
	freqs = sum(([key]*freq[key] for key in freq), [])

	# 3. Sort the quantities in reverse order, this helps identify False cases much faster.
	quantity.sort(reverse = True)

	# 4. Breadth First Search
	q = [(0, freqs)]
	while q:
		i, freqs = heapq.heappop(q)
		i = -i
		if i == len(quantity):
			return True
		quant = quantity[i]
		used = set()                                                                        # keep track of the freqs[j] values used for quantity[i]
		for j in range(len(freqs)):
			if freqs[j] >= quant and freqs[j] not in used:                                  # do not use the same value more than once for quantity[i]
				used.add(freqs[j])
				if freqs[j] > quant:
					heapq.heappush(q, (-(i+1), freqs[:j] + [freqs[j]-quant] + freqs[j+1:]))
				else:
					heapq.heappush(q, (-(i+1), freqs[:j] + freqs[j+1:]))                    # freqs[j] == quantity[i] we can stop checking other frequencies
					break
	return False
