    public boolean canDistribute(int[] nums, int[] q) {
        int[] cnts = new int[1001];
        for(int n:nums) cnts[n]++;
        for(int n:cnts)
            if(n>0) list.add(n);
        Arrays.sort(q); // try the largest quatity first
        return f(q, q.length-1);
    }
