class Solution {
public:
    vector<vector<int>> dp;
    int n, m;

    bool isPossible(int mask, int val, vector<int> & quantity){

        for(int i = 0; i < n; i++){
            if((mask & (1 << i))){
                if(val >= quantity[i])
                    val -= quantity[i];
                else 
                    return false;
            }
        }
        return true;
    }

    bool helper(int idx, int mask, vector<int> &quantity, vector<int> &arr){
        if(mask == (1 << n)-1) return true;
        if(idx == m) return false;

        if(dp[idx][mask] != -1) return dp[idx][mask];

        int tempMask = (~mask) & ((1 << n)-1);

		// to check among all the possible subsets of the elements not included currently in mask. 
        for(int s = tempMask; ; s = (s-1) & tempMask){
            if(isPossible(s, arr[idx], quantity)){
                if(helper(idx+1, mask | s, quantity, arr)) 
                    return dp[idx][mask] =  true;
            }
            if(s == 0) break;
        }

        return dp[idx][mask] = false;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        unordered_map<int, int> freq;

        for(int x : nums) freq[x]++;

        vector<int> arr;

        for(auto p : freq) arr.push_back(p.second);
        sort(arr.begin(), arr.end());
        sort(quantity.begin(), quantity.end());

        m = arr.size();
        n = quantity.size();

        dp.resize(m, vector<int>((1 << n), -1));

        return helper(0, 0, quantity, arr);
    }
};
