dp[index][state] denotes whether we can satisfy customers upto index = index , such that we have already satisfied some customers and our current state of frequency is state.  

```
class Solution {
public:
    map<vector<int> , int>dp ;
    int n ;
    vector<int> quant;
    map<int,int> freq ;
    bool solve(int index)
    {
        if(index == n) return true ;
        bool does = false;
        vector<int> currstate ;
        for(auto x : freq ) currstate.push_back(x.second);
        sort(currstate.begin(), currstate.end()) ;
        currstate.push_back(1e9+index);
        if(dp[currstate]){
            if(dp[currstate] == 1) return true;
            return false;
        }
        for(auto x : freq){
             int demand = quant[index];
             int curr = x.first;
             if(freq[curr] >= demand){
                freq[curr]-=demand;
                does = does | solve(index + 1);
                if(does == true){
                    dp[currstate] = 1;
                    return true;
                }
                freq[curr]+=demand;
             }
        }
        if(does == true) dp[currstate] = 1;
        if(does == false) dp[currstate] = 2;
        return does ;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        freq.clear();
        dp.clear() ;
        quant = quantity;
        for(auto x : nums ) freq[x]++;
        n = quantity.size();
        return solve(0);
    }
};

```

