class Solution {
    int sz = 50;

    public boolean canDistribute(int[] nums, int[] quantity) {
        int m = quantity.length;
        boolean[][] dp = new boolean[sz][1 << m];
        dp[0][0] = true;
        int[] freqs = new int[sz];
        Arrays.sort(nums);
        int prev = -1, ptr = -1;
        for(int i = 0; i < nums.length; i++) {
            if(nums[i] != prev) {
                prev = nums[i];
                ptr++;
            }
            freqs[ptr]++;
        }
        for(int ind = 1; ind <= sz - 1 && freqs[ind - 1] != 0; ind++) {
            int freq = freqs[ind - 1];
            for(int pre = 0; pre < (1 << m); pre++) {
                if(!dp[ind - 1][pre]) continue;
                dp[ind][pre] = true;
                for(int nxt = 0; nxt < (1 << m); nxt++) {
                    if((nxt & pre) != 0) continue;
                    int acc = 0;
                    for(int i = 0; i < m; i++) {
                        if((nxt & (1 << i)) != 0) {
                            acc += quantity[i];
                        }
                    }
                    if(acc > freq) continue;
                    dp[ind][nxt | pre] = true;
                    if((nxt | pre) == (1 << m) - 1) return true;
                }
            }
        }
        return false;
    }
}

