class OrderedStream {
    public:
        // Initialize "pointer" (btw not an actual pointer) & stream vector
        int ptr=0;
        vector<string> stream;

        OrderedStream(int n) {
            // Constructor makes stream vector of size n
            vector<string> strm(n);
            stream = strm;
        }

        vector<string> insert(int id, string value) {
            // decrease id by one to correspond with vector idx (i.e. start from 0)
            --id;

			// create a vector to be returned by function
            vector<string> returnVec;

			// at index id in stream vector, set the value
            stream[id] = value;

            // if stream at index ptr is not empty...
            if (stream[ptr] != "") {

				// create a new int to mark the start index
                int start_idx = ptr;

				// while stream at index ptr is not empty, move ptr to the next index
                while (stream[ptr] != "" && ptr != stream.size()) ptr++;

				// set returnVec to a sliced version of the stream vector
                // (i.e. stream[start_idx:ptr])
                returnVec = vector<string>(stream.begin()+start_idx, stream.begin()+ptr);
            }
            return returnVec;
        }
}; 

```

