struct OrderedStream {
    arr: Vec<Option<String>>,
    ptr: usize
}


/** 
 * `&self` means the method takes an immutable reference.
 * If you need a mutable reference, change it to `&mut self` instead.
 */
impl OrderedStream {

    fn new(n: i32) -> Self {
        Self {
            arr: vec![None; n as usize],
            ptr: 0
        }
    }

    fn insert(&mut self, id_key: i32, value: String) -> Vec<String> {
        let id_key = id_key as usize - 1;
        self.arr[id_key] = Some(value);

        if id_key > self.ptr {
            return vec![];
        }

        while let Some(Some(_)) = self.arr.get(self.ptr) {
            self.ptr += 1;
        }
        self.arr[id_key..self.ptr].iter_mut().map(|opt| opt.take().unwrap()).collect()
    }
}
