class OrderedStream {
private:
    int k; //pointer to min index
    vector<string> chunks; //chunky boy
public:
    OrderedStream(int n) {
        k = 1; //init to first pos

        for(int i = 0; i < n; i++) {
            chunks.push_back("-1"); //init all pos to -1 (unvisited)
        }

    }

    vector<string> insert(int idKey, string value) {
        vector<string> chunk{}; //output chonk
        chunks[idKey-1] = value; //replace -1 with actual value, 0-indexed

        if(idKey == k) { //if we are on our min pointer
            while(k <= chunks.size() && chunks[k-1] != "-1") { //and we are within bounds and haven't visited this index yet, 
                chunk.push_back(chunks[k-1]); //push to output chonk
                k++; //increase pointer, will increase to next "-1" unvisited index
            }
        }

        return chunk;
    }
};
```

