class OrderedStream {
public:
    int lastval = -1, n;
    vector<string> v;
    OrderedStream(int n) {
        v = vector<string>(n);
        this->n = n;
    }

    vector<string> insert(int idKey, string value) {
        v[idKey-1] = value;
        if(idKey - 1 - lastval == 1) {
            int idx = idKey-1;
            vector<string> temp;
            while(idx < n and v[idx].size()) temp.push_back(v[idx++]);
            lastval = idx-1;
            return temp;
        }
        return {};
    }
};

