class OrderedStream {
    Map<Integer, String> stream;
    int ptr; // intialize ptr, then find the longest contiguous increasing sequence of ids from the ptr
    public OrderedStream(int n) {
        this.stream = new HashMap<>();
        this.ptr = 1;
    }

    public List<String> insert(int idKey, String value) {
        stream.put(idKey, value);

        List<String> result = new ArrayList<>();

		// if the id which the ptr is currently pointing to, is populated, find the longest contiguous sequence of data (chunks) available, else return empty list
        while(stream.containsKey(ptr))
        {
		//increment the ptr to next id until we reach an id that is not yet populated. Next time we'll start reading data from that id if it gets populated.
            result.add(stream.get(ptr++));
        }

        return result;
    }
}

