import heapq

class OrderedStream:

    def __init__(self, n: int):
        self.idx = 1
        self.heap = []

    def insert(self, idKey: int, value: str) -> List[str]:
        result = []

        if idKey == self.idx:
            result.append(value)
            self.idx += 1

            while self.heap and self.heap[0][0] == self.idx:
                result.append(heapq.heappop(self.heap)[1])
                self.idx += 1
        else:
            heapq.heappush(self.heap, (idKey, value))

        return result
```

