class OrderedStream {`

    Map<Integer, String> idMap;
    Queue<Integer> queue;
    int lastPrintedIndex;

    public OrderedStream(int n) {
        idMap = new HashMap<>(n);
        queue = new PriorityQueue<>(); //Min heap Can we use a binary array instead? 
        lastPrintedIndex = 1;
    }

    public List<String> insert(int idKey, String value) {
        idMap.put(idKey, value);
        queue.offer(idKey);
        List<String> chunk = new ArrayList<>();

        while(!queue.isEmpty() &&lastPrintedIndex == queue.peek())
        {
            chunk.add(idMap.get(queue.poll()));
            lastPrintedIndex++;          
        }
        return chunk;
    }
