var OrderedStream = function(n) {
    // Initialize an array with n empty elements
    this.stream = new Array(n);
};

OrderedStream.prototype.insert = function(idKey, value) {
    // Set the chunk value of the stream at the given idKey
    // NOTE: Since idKey starts at 1, its corresponding index is idKey - 1
    const chunkIndex = idKey - 1;
    this.stream[chunkIndex] = value;

    // Initialize an empty result array
    const result = [];

    // Loop through all chunks in the stream or until an empty chunk is reached
    for (let i = 0; i < this.stream.length; i++) {

        // Get the value of the current chunk
        const chunkValue = this.stream[i];

        // If the index of the recently inserted chunk or a subsequent non-empty chunk is encountered, add its value to the result
        if (chunkValue !== undefined && i >= chunkIndex) {
            result.push(chunkValue)

          // If an undefined (empty) chunk is encountered, stop the loop
        } else if (chunkValue === undefined) {
            break;
        }

    }

    // Return the result array
    return result
};
