template <typename TInt>
inline TInt to_zero_indexed(const TInt one_indexed) {
    return one_indexed - 1;
}
//The reason I made this function is to keep intentions explicit to maximize readability. 
//Also, I made it a template for generality, on principle (not that it matters much here).

class OrderedStream {
private:
    vector<string> data;
    size_t current_stream_pos = 0;  //one past the current contiguous data segment
public:
    OrderedStream(const int elem_count) :
        data(elem_count)
    { }

    size_t scan_for_contiguous_index() {
        for (size_t i = 0; i < data.size(); ++i) {
            if (data[i].empty()) { return i; }
        }
        return data.size();
    }

    vector<string> insert(
        const int id_key, 
        const string value
    ) {
        //assert(to_zero_indexed(id_key) >= 0);
        //assert(to_zero_indexed(id_key) < data.size());
        //I'd leave these assertions in if this were production code.

        data[to_zero_indexed(id_key)] = value;

        const size_t prev_stream_pos = current_stream_pos;
        current_stream_pos = scan_for_contiguous_index();

        return vector<string>(
            data.begin() + prev_stream_pos, 
            data.begin() + current_stream_pos
        );
    }
};

