class Solution {
public:
    bool closeStrings(string word1, string word2) {
        if(word1.length()!=word2.length())
            return false;
        vector<int> hash1(26,0);
        vector<int> hash2(26,0);
        for(int i=0;i<word1.length();i++){
            hash1[word1[i] - 'a']++;
        }
        for(int i=0;i<word2.length();i++){
            if(hash1[word2[i]-'a']==0) //cheking for new char present in word2  (new char means that is not present in word1)
                return false;
            hash2[word2[i]-'a']++;
