class Solution {
public:
    bool closeStrings(string word1, string word2) {

        if(word1.length() != word2.length())
        return false ;

        vector<int> f1(26 , 0) , f2(26 , 0);

        //Store The Count of Each Letter from both words separately 
        for(int i = 0 ; i<word1.length() ; i++)
        {
            f1[word1[i]-'a']++;
            f2[word2[i]-'a']++;
        }

        //Check For wheather a letter is present in one word and absent in 
        // another word , then return false
        // Because we can't interchange that letter with any other word
        // ex : abcdef abcde will always return false

        for(int i = 0 ; i<26 ; i++)
        {
            if (f1[i] == 0 ^ f2[i] == 0)
                return false;
        }

        // Now Sort The frequencies 
        sort(f1.begin() , f1.end());
        sort(f2.begin() , f2.end());

        // If they don't match return false
        for(int i = 0 ; i<26 ; i++)
        {
            if(f1[i] != f2[i])
                return false;
        }

        return true;


    }
};
