public class Solution {
    public bool CloseStrings(string word1, string word2) {
        if(word1.Length != word2.Length)
            return false;

        var freq1 = GetFrequency(word1);
        var freq2 = GetFrequency(word2);

        if(!IsAnagram(freq1, freq2))
            return false;

        Array.Sort(freq1);
        Array.Sort(freq2);

        for(int idx = 0; idx < 26; idx++)
            if(freq1[idx] != freq2[idx])
                return false;

        return true;
    }

    private bool IsAnagram(int[] freq1, int[] freq2) {
        for(int idx = 0; idx < 26; idx++)
            if((freq1[idx] == 0 && freq2[idx] != 0) || (freq1[idx] != 0 && freq2[idx] == 0))
                return false;

        return true;
    }

    private int[] GetFrequency(string str) {
        var freq = new int[26];
        foreach(var chr in str)
            freq[chr - 'a']++;

        return freq;
    }
}
