class Solution {
public:
    bool closeStrings(string word1, string word2) {

        if(word1.length()!=word2.length()){
            return false;
        }

        unordered_map<char,int> m1;
        unordered_map<char,int> m2;

        priority_queue<int,vector<int>,greater<int>> pq1;
        priority_queue<int,vector<int>,greater<int>> pq2;

        for(auto c:word1){
            m1[c]++;
        }

        for(auto val:m1){
            pq1.push(val.second);
        }

        for(auto c:word2){
            m2[c]++;
        }

        for(auto val:m2){
            pq2.push(val.second);
        }

        for(auto key:m1){
            if(m2.find(key.first)!=m2.end()){
                int top1=pq1.top();
                int top2=pq2.top();
                if(top1!=top2){
                    return false;
                }
                pq1.pop();
                pq2.pop();
            }
            else{
                return false;
            }
        }
        return true;
    }
};
