class Solution {
    public boolean closeStrings(String word1, String word2) {
        if(word1.length() != word2.length()) {
            return false;
        }

        int[] charFreq1 = new int[26];
        int[] charFreq2 = new int[26];        
        for(int idx = 0; idx<word1.length(); idx++) {
            charFreq1[word1.charAt(idx) - 'a']++;
        } for(int idx = 0; idx<word2.length(); idx++) {
            charFreq2[word2.charAt(idx) - 'a']++;
        }

        Map<Integer, Integer> freqToCount = new HashMap<>();
        for(int idx = 0; idx<26; idx++) {
            int freq1 = charFreq1[idx];
            int freq2 = charFreq2[idx];

            if((freq1==0 && freq2!=0) || (freq1!=0 && freq2==0) ) {
                return false;
            }

            freqToCount.put(freq1, freqToCount.getOrDefault(freq1, 0) + 1);
            freqToCount.put(freq2, freqToCount.getOrDefault(freq2, 0) - 1);
        }

        for(int value : freqToCount.values()) {
            if(value != 0) {
                return false;
            }
        }

        return true;
    }
}
