class Solution {
public:
    bool closeStrings(string A, string B) {
        // if their length is not equal they can't be close

        if(A.length() != B.length()) return false;

        // calculate the occurance of every element

        int a[26]={},b[26]={};
        for(auto it : A){
            a[it-'a']++;
        }
        for(auto it : B){
            b[it-'a']++;
        }

        // check for element present in only one string

        for(int i = 0;i < 26;i++){
            if((a[i] == 0 && b[i] != 0) || (a[i] != 0 && b[i] == 0)){
                return false;
            }
        }

        // check if total frequency can be mapped

        map<int,int>m;
        for(int i = 0;i < 26;i++){
            m[a[i]]++;
        }
        for(int i = 0;i < 26;i++){
            m[b[i]]--;
        }

        // if the frequency is mapped perfectly then the value for each key will be utilized

        for(auto it : m){
            if(it.second != 0) return false;
        }

        return true;
    }
};
