class Solution {
public:
    const int sizeOfAlphabet=26;
    bool closeStrings(string word1, string word2) {
        int sizeOfWord1 = word1.size();
        int sizeOfWord2 = word2.size();
        int seen1=0,seen2=0;
        vector<int> charFrequenciesOfWord1(sizeOfAlphabet, 0);
        vector<int> charFrequenciesOfWord2(sizeOfAlphabet, 0);

        if(sizeOfWord1 != sizeOfWord2)
        {
            return false;
        }

        for(int i = 0; i < sizeOfWord1; i++)
        {
            charFrequenciesOfWord1[word1[i]-97]++;
            charFrequenciesOfWord2[word2[i]-97]++;
            seen1|=(1 << word1[i] - 97);
            seen2|=(1 << word2[i] - 97);          
        }

        sort(begin(charFrequenciesOfWord1),end(charFrequenciesOfWord1));
        sort(begin(charFrequenciesOfWord2),end(charFrequenciesOfWord2));

        return charFrequenciesOfWord1==charFrequenciesOfWord2 && seen1==seen2;
    }
};
