        char[] chArray1 = word1.toCharArray();
        char[] chArray2 = word2.toCharArray();

        int[] freq1 = new int[26];
        int[] freq2 = new int[26];

        calculateFrequency(chArray1, freq1);
        calculateFrequency(chArray2, freq2);

        if(validateForOperation1(freq1, freq2)) return true;

		//Below is the validation for Operation-2

		//Operation 2 allows you to freely reassign the letters' frequencies.

        for(int i=0; i<26; i++)
        {
            if((freq1[i]!=0 && freq2[i]==0) || (freq2[i]==0 && freq2[i]!=0))
                return false;            
