        Arrays.sort(freq1);
        Arrays.sort(freq2);

        for(int i=0; i<26; i++)
        {
            if(freq1[i] != freq2[i]) return false;
        }
        return true;
    }

    public boolean validateForOperation1(int[] freq1, int[] freq2)
    {
		//Operation 1 allows you to freely reorder the string.
