class Solution {
    public boolean closeStrings(String word1, String word2) {
        int len1 = word1.length();
        int len2 = word2.length();
        if (len1 != len2) return false;
        Map<Character, Integer> hm1 = new HashMap();
        Map<Character, Integer> hm2 = new HashMap();
        ArrayList<Integer> a = new ArrayList();
        ArrayList<Integer> b = new ArrayList();

        for(int i = 0; i < len1; i++) {
            hm1.put(word1.charAt(i), 1+hm1.getOrDefault(word1.charAt(i), 0));
        }

        for(int i  = 0; i < len1; i++) {
            if(hm1.containsKey(word2.charAt(i))) {
                hm2.put(word2.charAt(i), 1+hm2.getOrDefault(word2.charAt(i), 0));
            } else {
                return false;
            }
        }

        // add frequency(values) from maps to arrays 
        a = new ArrayList(hm1.values());
        b = new ArrayList(hm2.values());

        Collections.sort(a);
        Collections.sort(b);

        return a.equals(b);
    }
}
