    public boolean closeStrings(String word1, String word2) {
        if(word1.length() != word2.length())
            return false;

        HashMap<Character, Integer> hm1 = new HashMap<>();
        HashMap<Character, Integer> hm2 = new HashMap<>();

        for(int i = 0 ; i < word1.length(); i++){
