                return false;       //indicates that ('a'+i) char is only in one string 
        }

        sort(c1.begin(), c1.end());
        sort(c2.begin(), c2.end());

        //count of char must be same, we dont care which one as it is always possible to trnsfrom one into another
        for(i=0;i<26;i++)
            if(c1[i]!=c2[i])
                return false;

        return true;
    }
};
```

