//Brute force would be O(n^2) but taking adavantage of the sorting, we know that lowest value will be leftmost and highest will be rightmost.


class Solution {

    public int[] twoSum(int[] numbers, int target) {

        int left=0,right=numbers.length-1;

        while(left<right)
        {
            int sum=numbers[left]+numbers[right];
            if(sum==target)
                return new int[]{left+1,right+1};
            else if(sum>target)
                right--;
            else if(sum<target)
                left++;
        }
        return new int[]{};
    }
}



