                if numbers[m] == target:
                    return m
                elif numbers[m] < target:
                    l = m + 1
                else:
                    r = m - 1
            return -1

        for left, num in enumerate(numbers):
            right = binarySearch(left + 1, len(numbers)-1, target - num)
            if right != -1:
                return [left + 1, right + 1]
