                if(numbers[i]+numbers[mid]==target)
                {
                    if(mid==i)
                    {
                        if(numbers[i]+numbers[mid+1]==target)
                            mid++;
                        else if(numbers[i]+numbers[mid-1]==target)
                            mid--;
                    }
                    return vector<int>{i+1,mid+1};
