class Solution {
public:
    // USING BINARY SEARCH - nLogn

    int bs(vector<int> &nums, int i ,int j,int target){
        int l = i, h = j;
        int mid;

        while(l<=h)
        {
            mid = l + (h-l)/2;
            if(nums[mid] == target)
                return mid;
            else if(nums[mid]>target){
                h = mid-1;
            }else
                l = mid+1;
        }

        return INT_MAX;            
    }

    vector<int> twoSum(vector<int>& nums, int target) {

        vector<int> ans;
        int n = nums.size();

        for(int i = 0; i<n-1; i++)  //IMP 
        {   
            if(nums[i]>0 and nums[i]>target)        //condition for positive numbers only
                break;

            int index = bs(nums,i+1,n-1,target - nums[i]);

            if(index!= INT_MAX){
                ans.push_back(i+1);
                ans.push_back(index+1);
            }
        }

        return ans;

    }
};

