    public int[] twoSum(int[] nums, int target) {
        int i = 0, j = nums.length - 1;

        while(i < j) {
            int m = i + (j - i) /  2;
            int sum = nums[i] + nums[j];

            if(sum == target) 
                return new int[] {i+1, j+1};

            if(sum >= target)
                if(nums[i] + nums[m] > target)
                    j = m - 1;
                else
                    j--;
            else
                if(nums[m] + nums[j] < target)
                    i = m + 1;
                else
                    i++;
        }

        return null;
    }
