 * // This is the Master's API interface.
 * // You should not implement it, or speculate about its implementation
 * class Master {
 *   public:
 *     int guess(string word);
 * };
 */
class Solution {
public:
    //This API returns list of strings
    //whose match count was exactly same as cnt
    vector<string> getList(int cnt, vector<string> words, string cur) {
        vector<string> res;
        res.clear();
        for (string w: words) {
            int match = 0;
            for (int i = 0; i < 6; ++i) {
                if(w[i] == cur[i]) ++match;
            }
            if(cnt == match) res.push_back(w);
        }
        return res;
    }

    //This API returns the candidate word to be selected.
    string getContenderWord(vector<string> word) {
        int n = word.size();
        int dp[6][26];
        memset(dp, 0, sizeof dp);
        for (int i = 0; i < n; ++i) {
            //count the frequency at each index
            for (int j = 0; j < 6; ++j) {
                ++dp[j][word[i][j] - 'a'];
            }
        }
        string result = "";
        int best_score = 0;
        for (int i = 0; i < n; ++i) {
            int  cur_score = 0;
            for (int j = 0; j < 6; ++j) {
                cur_score += dp[j][word[i][j] - 'a'];
            }
            if(cur_score > best_score) {
                best_score = cur_score;
                result = word[i];
            }
        }
        return result;
    }

    void findSecretWord(vector<string>& wordlist, Master& master) {
        int cnt = 0;
		//need to query only 10 times, after that 
		//querying doesn't make any sense
        while(cnt < 10) {
            string cur = getContenderWord(wordlist);
            int need = master.guess(cur);
            if(need == 6) return ;
            wordlist = getList(need, wordlist, cur);
            ++cnt;
        }
    }
};
