class Solution {
    private int[][] simi;
    private int N;
    private String[] words;
    private HashSet<Integer> used;
    private List<Integer> candi;
    public void findSecretWord(String[] wordlist, Master master) {
        N = wordlist.length;
        words = wordlist;
        simi = new int[N][N];
        for(int i=0; i<N; ++i) {
            for(int j=i+1; j<N; ++j) {
                simi[i][j] = simi[j][i] = compare(i, j);
            }
        }
        used = new HashSet<>();
        candi = new ArrayList<>();
        for(int i=0; i<N; ++i) candi.add(i);

        int counter = 0;

        while(true) {
            int guess = next();
            int thisSim = master.guess(words[guess]);

            ++counter;
            // System.out.println(counter + " " + guess);

            if(thisSim == 6) return;

            used.add(guess);
            List<Integer> candi2 = new ArrayList<>();
            for(int i:candi) if(simi[guess][i] == thisSim) candi2.add(i);
            candi=candi2;
        }
    }


    private int next() {
        int res = -1;
        int minVotes = candi.size();

        for(int i=0; i<N; ++i) {
            if(used.contains(i)) continue;

            int[] votes = new int[7];

            for(int j:candi) {
                if(j==i) continue;
                ++votes[simi[j][i]];
            }

            int thisMaxVotes = votes[0];
            for (int j = 0; j < 7; ++j) thisMaxVotes = Math.max(thisMaxVotes, votes[j]);

            if(minVotes > thisMaxVotes) {
                minVotes = thisMaxVotes;
                res = i;
            }
        }

        return res;
    }

    private int compare(int i, int j) {
        int res = 0;
        for(int k=0; k<words[i].length(); ++k) if(words[i].charAt(k) == words[j].charAt(k)) ++res;
        return res;
    }
}
