class Solution
{
    public void FindSecretWord(string[] wordlist, Master master)
    {
        // Sort the input word list
        Array.Sort(wordlist);

        // Create a mapping between words that failed the test and their score
        Dictionary<string, int> badWords = new Dictionary<string, int>();
        foreach(string word in wordlist) 
        {  
            if (ValidateWord(word, badWords))
            {
                int score = master.guess(word);

                if (score==6) 
                    return;

                badWords.Add(word, score);
            }
        }  
    }

    private bool ValidateWord(string candidate, Dictionary<string, int> badWords) 
    {
        int similarity = 0;
        int score = 0;

        // Take the current word and compare it to each of the previously explored bad words
        foreach(string w in badWords.Keys)
        {
            // check the similarity of the current word and current bad word - this is the number of common letters
            similarity = Similarity(w, candidate);

            // recall the score of the currently explored bad word
            score = badWords[w];

            // If candidate has letters in common with a word that received 
            // a score of 0, that means we can be certain that the candidate word 
            // contains letters that are not in the target. For this reason,
            // we know for certain candidate is not the target word. 
            bool overlapWithBadWord = (score == 0 && similarity > 0);


            if (overlapWithBadWord || similarity < score )
                return false;
        }
        return true;
    }


    public int Similarity(string s1, string s2) 
    {
        // Get the number of letters that overlap between two words

        int count = 0;

        for(int i = 0; i < s1.Length; i++) 
          if (s1[i] == s2[i]) 
              count++;

        return count;
    }
}

