                    wordlist[j] = wordlist[pop-1];
                    pop--;
                    j--;
                }
            }
        }
        return;

    }

    private int Match(string guess, string word) {
        int n = 0;
        for (int i = 0; i < 6; i++) {
            if (guess[i] == word[i]) n++;
        }
        return n;
    }
}
