class Solution {
    public void findSecretWord(String[] wordlist, Master master) {
        Set<String> candidateWords = new HashSet<>();
        candidateWords.addAll(Arrays.asList(wordlist));

        while(!candidateWords.isEmpty()) {
            final String word = candidateWords.iterator().next();
            candidateWords.remove(word);

            int similarity = master.guess(word);
            if (similarity == 6) {
                return;
            }
            candidateWords.removeIf(w -> getSimilarity(word, w) != similarity);
        }
    }

    private int getSimilarity(String a, String b) {
        int result = 0;
        for(int i = 0 ; i < 6 ; i++) {
            if (a.charAt(i) == b.charAt(i)) {
                result++;
            }
        }
        return result;
    }
}
