class Solution {
public:
    void findSecretWord(vector<string>& wordlist, Master& master) {
        while(wordlist.size() > 0) {
            string guess_word = getBestWord(wordlist);
            int guess_match_score = master.guess(guess_word);

            if(guess_match_score == 6) {
                return;
            }

            wordlist = eliminateWords(wordlist, guess_word, guess_match_score);
        }
