        vector<vector<int>> store_count(6, vector<int>(26, 0));

        for(string word : wordlist) {
            for(int i = 0; i < 6; i++) {
                store_count[i][word[i] - 'a']++;
            }
        }

        int best_score = 0;
        string best_word = "";

        for(string word : wordlist) {
            int cur_score = 0;
            for(int i = 0; i < 6; i++) {
                cur_score += store_count[i][word[i] - 'a'];
