class Solution {
public:
    void findSecretWord(vector<string>& wordlist, Master& master) {

        int N = wordlist.size();

        // sort wordlist, the first and the last word have the smallest similarity. 
        sort(begin(wordlist), end(wordlist));

        // track visited items
        int visited[N];
        memset(visited, 0, sizeof(visited));

        for(int guesscnt = 0, flag = 1; guesscnt < 10; guesscnt ++, flag ^= 1) {

            // each item we start from the word with the smallest similarity
            // to do that, we flip flag each time
            int i;
            if (flag) {
                // search from begin
                i = 0;
                while(i < N && visited[i]) i++;
                if (i == N) return;
            }
            else {
                // search from end
                i = N - 1;
                while(i >= 0 && visited[i]) i--;
                if (i == -1) return;
            }

            string curr = wordlist[i];
            int cnt = master.guess(curr);
            visited[i] = 1;

            if (cnt == 6) return;

            // remove all the words with different similarity
            for (int j = 0; j < N; j++) {
                if (visited[j]) {
                    continue;
                }

                string next = wordlist[j];

                int overlaps = 0;

                for (int a = 0; a < 6; a++) {
                    overlaps += next[a] == curr[a];
                }

                if (overlaps != cnt) {
                    visited[j] = 1;
                }
            }
        }
    }
};
