class Solution {
    public void findSecretWord(String[] wordlist, Master master) {
        Random rand = new Random();
        int n = wordlist.length;
        int cnt = 10;
        int guess = 0;
        while (cnt > 0 && n > 0 && guess != 6) {
            cnt--; 
            int target = rand.nextInt(n);
            String targetStr = wordlist[target];
            guess = master.guess(wordlist[target]);
            for (int i = n - 1; i >= 0; i--) {
                if (i == target) {
                    swap(wordlist, i, n - 1);
                    n--;
                    continue;
                }
                if (likeness(targetStr, wordlist[i]) != guess) {
                    swap(wordlist, i, n - 1);
                    n--;
                }
            }
        }
    }

    public void swap(String[] ls, int i, int j) {
        String temp = ls[i];
        ls[i] = ls[j];
        ls[j] = temp;
    }

    public int likeness(String base, String w) {
        int ret = 0;

        for (int i = 0; i < base.length(); i++) {
            if (base.charAt(i) == w.charAt(i)) {
                ret++;
            }
        }

        return ret;
    }
}
