class Solution {
public:
    int countMatches(const string& word1, const string& word2){
        int count=0;
        for(int i=0;i<word1.size();i++)
        {
            if(word1[i]==word2[i])
                count++;
        }
        return count;
    }

    void Prune(const string& word, const int& n, set<string>& wlist){
        set<string> wtoDel;
        for(const auto& element:wlist){
            if(countMatches(element,word)!=n)
                wtoDel.insert(element);
        }
        for(const auto& element:wtoDel)
        {
            wlist.erase(element);
        }
    }

    void findSecretWord(vector<string>& wordlist, Master& master) {
        set<string> wlist{wordlist.begin(),wordlist.end()};
        int c=0;
        while(!wlist.empty() && c<10)
        {
            int n = master.guess(*wlist.begin());
            Prune(*wlist.begin(),n,wlist);
            c++;
        }
    }
}; 
