		return not any(word1[i] in bad[i] for i in range(6))

	def match_filter(word1):
		'''If word1 does not match EXACTLY score characters in each guessed word - return False - word 1 CANNOT be the secret word'''
		return all(match_score(word1, word2) == score for word2, score in guessed.items())

