/**
 * // This is the Master's API interface.
 * // You should not implement it, or speculate about its implementation
 * interface Master {
 *     public int guess(String word) {}
 * }
 */
class Solution {
    public void findSecretWord(String[] wordlist, Master master) {
        Set<String> set = new HashSet<>();

        for (String s : wordlist) {
            set.add(s);
        }

        int i = 10;
        while(i-- > 0) {
            String g = getRandom(set);
            int n = master.guess(g);

            if (n == 6) {
                return;
            }

            filterSet(g, n, set);
        }
    }

    private void filterSet(String g, int n, Set<String> set) {
        Set<String> st = new HashSet<>();
        for (String s : set) {
            if (equalCorrect(s, g) != n) {
                st.add(s);
            }
        }

        for (String s : st) {
            set.remove(s);
        }
    }

    private int equalCorrect(String s, String g) {
        int count = 0;
        for (int i = 0; i < 6; i++) {
            if (s.charAt(i) == g.charAt(i)) {
                count++;
            }
        }

        return count;
    }

    private String getRandom(Set<String> set) {
        for (String s : set) {
            return s;
        }

        return "";
    }
}
