class Solution {
public:
    bool checkStraightLine(vector<vector<int>>& coordinates) {
        // If the number of coordinates is less than 2, it cannot form a straight line
        if (coordinates.size() < 2) {
            return false;
        }

        // Calculate the slope between the first two points
        int x1 = coordinates[0][0];
        int y1 = coordinates[0][1];
        int x2 = coordinates[1][0];
        int y2 = coordinates[1][1];
        int dx = x2 - x1;
        int dy = y2 - y1;

        // Check the slope between the remaining points
        for (int i = 2; i < coordinates.size(); i++) {
            int xi = coordinates[i][0];
            int yi = coordinates[i][1];

            // If the slope between any two points is different, it's not a straight line
            if (dx * (yi - y1) != dy * (xi - x1)) {
                return false;
            }
        }

        // All slopes are equal, so it's a straight line
        return true;
    }
};
