impl Solution {
    pub fn check_straight_line(coordinates: Vec<Vec<i32>>) -> bool {
        // y2 - y1 / x2 - x1 = y3 - y2 / x3 - x2
        // = (y2 - y1) * (x3 - x2) = (y3 - y2) * (x2 - x1)
        for w in coordinates.windows(3) {
            if ((w[1][1] - w[0][1]) * (w[2][0] - w[1][0]) != (w[2][1] - w[1][1]) * (w[1][0] - w[0][0])) {
                return false;
            }
        }
        true
    }
}
