class Solution {
public:
    bool checkStraightLine(vector<vector<int>>& coordinates) {
        //(y2-y1) = slope * (x2-x1)
		//slope = (y2-y1)/(x2-x1)
		//(y2'-y1')*(x2-x2) == (x2'-x1')*(y2-y1)
        int y = (coordinates[1][1]-coordinates[0][1]);
