class Solution {
public:
    bool checkStraightLine(vector<vector<int>>& coordinates) {

        double slope;

        if(coordinates[1][0]-coordinates[0][0] == 0) // if denominator is zero then make slope infinity(INT_MAX)
            slope= INT_MAX;                          // above step is required because in c++ runtime error will occur
        else
            slope= (coordinates[1][1]-coordinates[0][1])*1.0/((coordinates[1][0]-coordinates[0][0])*1.0);

        if(coordinates.size()==2)
           return true;

        for(int i=1;i<coordinates.size();i++)
        {
            double currSlope;

            if(coordinates[i][0]-coordinates[0][0] == 0) // if denominator is zero then make slope infinity(INT_MAX)
                currSlope=INT_MAX;
            else
                currSlope= ((coordinates[i][1] -coordinates[0][1])*1.0)/((coordinates[i][0]-coordinates[0][0])*1.0);

            if(currSlope != slope)
                return false;
        }

        return true;

    }
};
