	        for(int j = 0 ; j < i ; j++)
	        {
	            if(nums[j] < nums[i])
	            {
	                if(dp1[j] > ma)
	                {
	                   ma = dp1[j];
	                }
	            }
	        }
	        dp1[i] = ma + 1;
