        for(int i=1;i<size-1;i++){
		//bcz there might be a condition where the sum is higher but its not a mountain( \ ) but we have to choose a mountain( ^ ).
            if(lis[i]>1 && ldp[i]>1)
            ans=max(ans,(lis[i]+ldp[i])-1);
        }
        return size-ans;
    }
};
