/**
    A different version of longest increasing subsequence
    Find LIS from front
    Find LIS from back
    find a position where sum of the two is max..
    ans = (totalLen - left+right-1)
**/
class Solution {
    public int minimumMountainRemovals(int[] nums) {
        int n = nums.length, max = 0;

        int[] front = new int[n+1];
        Arrays.fill(front, 1);

        int[] back = new int[n+1];
        Arrays.fill(back, 1);

        for(int i=0 ; i<n ; i++){
            for(int j=0 ; j<i ; j++){
                if(nums[i] > nums[j] && front[i] < front[j] + 1){
                    front[i] = front[j] + 1;
                }
            }
        }

        for(int i=n-1 ; i>=0 ; i--){
            for(int j=n-1 ; j>i ; j--){
                if(nums[i] > nums[j] && back[i] < back[j] + 1){
                    back[i] = back[j] + 1;
                }
            }
			//find position where sum of the front[i] & back[i] is max (-1 to not include the mountain peak twice)
            if(back[i] > 1 && front[i] > 1)
                max = Math.max(max, back[i] + front[i]-1);
        }
        return n - max;
    }
}

